import React from 'react';
import profile from '../assets/images/profile1.png'

const Comments = (props) => {
    return (
        <div style={styles.container}>
            <div>
                <img src={profile} style={styles.profile} />
            </div>
            <div style={styles.contentContainer}>
                <div style={styles.info}>
                    <span style={styles.nickname}>{props.nickname}</span>
                    <span style={styles.time}>{props.time}</span>
                </div>
                <span style={styles.content}>{props.content}</span>
                <button style={styles.button}>답글 달기</button>
            </div>

        </div>
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flexDirection: 'row',
        padding: 4
    },
    profile: {
        width: 50,
        height: 50,
        borderRadius: 25
    },
    contentContainer: {
        display: 'flex',
        flexDirection: 'column',
        marginLeft: 16,
        marginTop: 4
    },
    info: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        gap: 8,
        marginBottom: 4
    },
    nickname: {
        color: '#2D2D2D',
        fontSize: 16,
        fontWeight: 800
    },
    time: {
        color: '#9E9E9E',
        fontSize: 12,
        fontWeight: 500
    },
    content: {
        maxWidth: 600,
        marginBottom: 4,
        color: '#2D2D2D',
        fontSize: 16,
        fontWeight: 500,
        textAlign: 'left'
    },
    button: {
        background: 'white',
        border: 'none',
        color: '#9E9E9E',
        fontSize: 12,
        textAlign: 'left'
    }
})

export default Comments;
