const inputfield = (props) => {
    return (
        <div style={{ display: 'flex', flexDirection: 'row', gap: 24, alignItems: 'center' }}>
            <div style={{ color: '#808080', fontSize: 20, fontWeight: 600 }}>{props.title}</div>
            <input
                type="text"
                placeholder={props.placeholder}
                style={{
                    width: 900,
                    height: 40,
                    paddingLeft: 16,
                    borderRadius: 8,
                    borderWidth: 1.5,
                    borderStyle: 'solid',
                    borderColor: '#B6B6B6',
                    outlineColor: '#FF7474',
                }} />
        </div>
    )
}

export default inputfield;
