const inputfield = (props) => {
    return (
        <div style={styles.container}>
            <div style={styles.title}>{props.title}</div>
            <input
                className="inputField"
                type="text"
                placeholder={props.placeholder}
                style={styles.input} />
        </div>
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        gap: 24,
    },
    title: {
        color: '#808080',
        fontSize: 20,
        fontWeight: 600
    },
    input: {
        width: 900,
        height: 40,
        paddingLeft: 16,
        borderRadius: 8,
        borderWidth: 1.5,
        borderStyle: 'solid',
        borderColor: '#B6B6B6',
        outlineColor: '#FF7474'
    },
});


export default inputfield;
