import React from "react";

import heart_off from '../assets/icons/favorite_off.png';
import heart_on from '../assets/icons/favorite_on.png';
import comment from '../assets/icons/comment.png';
import share from '../assets/icons/share.png';
import person from '../assets/icons/person.png';

const StudyPost = () => {
    return (
        <div>
            <div style={{ display: 'flex', flexDirection: 'row', gap: 10 }}>
                <div style={{ background: '#FF7474', borderRadius: 25, paddingTop: 4, paddingBottom: 4, paddingLeft: 8, paddingRight: 8, color: 'white', fontSize: 12, fontWeight: 600 }}>모집중</div>
                <div>정보보안기사 온라인 스터디</div>
            </div>
            <div>정보처리기사 온라인 스터디 모집합니다! 디코에서 주 3회 스터디 진행할 예정입니다!!</div>
            <div>
                <div>정보보안기사</div>
            </div>
            <div>
                <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', gap: 8 }}>
                    <img src={person} style={{ width: 14, height: 14 }} />
                    <div style={{ color: '#606060', fontSize: 12, fontWeight: 500 }}>4명</div>
                    <div style={{ width: 2, height: 2, background: '#606060', borderRadius: 25 }} />
                </div>

            </div>
        </div>
    )
}

export default StudyPost;
